// SPDX-License-Identifier: MIT

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

library TransferHelper {
    using SafeERC20 for IERC20;

    /**
     * @notice Extension of SafeERC20 to transfer an amount with a fee sent to a specified recipient.
     * @param token The ERC20 token contract address to use.
     * @param from The address of the sender.
     * @param to The address of the primary receiver.
     * @param amount The amount to send to the primary receiver.
     * @param feeRecipient The address of the fee recipient.
     * @param feeAmount The fee amount to send the fee recipient from the sender.
     */
    function transferFromWithFee(
        IERC20 token,
        address from,
        address to,
        uint256 amount,
        address feeRecipient,
        uint256 feeAmount
    ) internal {
        // Do the actual transfer
        token.safeTransferFrom(from, to, amount);

        // Transfer the fee
        token.safeTransferFrom(from, feeRecipient, feeAmount);
    }
}
